
public class FlowControl {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
int a =140,b=75,c=150;

if(a>=b && a>=c)
{
	System.out.println("Largest number of 3 is  :" +a);
}

if(b>=a && b>=c)
{
	System.out.println("Largest number of 3 is  :" +b);
}

if(c>=a && c>=b)
{
	System.out.println("Largest number of 3 is  :" +c);	
}
	}
}
